mapper.select:
SELECT bbseditno, title, bbs_date, hit 
FROM (
 SELECT rownum r, a.* 
  FROM (
   SELECT bbseditno, title, bbs_date, hit 
   FROM bbsedit 
   WHERE bbscd = #{bbscd} 
   ORDER BY bbseditno DESC) a
)
WHERE r BETWEEN #{start} AND #{end}

mapper.update:
UPDATE bbsedit
SET
title=#{title},contents=#{contents}
WHERE bbseditno= #{bbseditno}
