mapper.select:
SELECT bbseditno, title, bbs_date, hit, attachfilenum, commentno FROM ( SELECT rownum r, a.* FROM ( SELECT a.bbseditno, title, a.bbs_date, hit, count(distinct(f.attachfileno)) attachfilenum, count(distinct(c.commentno)) commentno FROM bbsedit a, attachfile f, comments c WHERE a.bbseditno = f.bbseditno(+) AND a.bbseditno = c.bbseditno(+) AND bbscd = #{bbscd}
AND (title LIKE '%${searchWord}%' OR contents LIKE '%${searchWord}%')
GROUP BY a.bbseditno, title, a.bbs_date, hit ORDER BY bbseditno DESC ) a ) WHERE r BETWEEN #{start} AND #{end}

mapper.update:
UPDATE bbsedit
SET
title=#{title},contents=#{contents}
WHERE bbseditno= #{bbseditno}

mapper.select:
SELECT commentno,bbseditno,memId,memo,bbs_date
FROM comments
WHERE commentno = #{commentno}
